from selenium import webdriverfrom selenium.webdriver.firefox.options import Options as FirefoxOptionsfrom selenium.webdriver.firefox.service import Servicefrom selenium.webdriver.firefox.webdriver import WebDriverdef get_firefox_browser() -> WebDriver:    options = FirefoxOptions()    options.headless = True    options.add_argument("--disable-notifications")    options.add_argument("disable-infobars")    geckodriver_path = '/geckodriver/geckodriver'    service = Service(executable_path=geckodriver_path)    browser: WebDriver = webdriver.Firefox(options=options, service=service)    browser.implicitly_wait(10)  # wait 10 seconds for all DOM elements    return browser