import osimport subprocessfrom selenium import webdriverfrom selenium.webdriver.chrome.options import Optionsfrom selenium.webdriver.chrome.service import Servicefrom selenium.webdriver.chrome.webdriver import WebDriverdef get_browser() -> WebDriver:    options = Options()    options.add_argument("--disable-notifications")    options.add_argument("--disable-infobars")    options.add_argument('--headless=new')  # Enable headless mode    options.add_argument("--disable-gpu")  # Disable GPU    options.add_argument("--no-sandbox")  # Bypass OS security model    chromedriver_path = os.path.join("/chromedriver/chromedriver")    service = Service(executable_path=chromedriver_path, log_output=subprocess.STDOUT)    browser: WebDriver = webdriver.Chrome(service=service, options=options)    browser.implicitly_wait(10)  # wait 10 seconds for all DOM elements    return browser